from bs4 import BeautifulSoup
import requests
import json

#1. 현재 실시간 날씨, 실시간 미세먼지 정보 불러오기
def get_weather(latitude, longitude):
    url = f'https://api.open-meteo.com/v1/forecast?latitude={latitude}&longitude={longitude}&current_weather=true&timezone=auto'
    response = requests.get(url)
    json_data = json.loads(response.text)
    print(json_data["current_weather"]['temperature'])

def get_pm25weather(sidoName):
    params = {
        'serviceKey': 'dFIHe0liJzwfyJHUvf1BDJoJbvGh4AoCY9PSIr1n4x1txxNeZO7gXSoAPXEAGfbcXUUMwdC4DJmIw/8ldBCN6A==',
        'returnType': 'json',
        'numOfRows': '100',
        'pageNo': '1',
        'sidoName': sidoName,
        'ver': '1.0',}

    response = requests.get('http://apis.data.go.kr/B552584/ArpltnInforInqireSvc/getCtprvnRltmMesureDnsty', params=params)
    json_data = json.loads(response.text)
    airinfo_list = json_data['response']['body']['items']
    result_list = []
    for airinfo in airinfo_list:
        result_list.append([airinfo['sidoName'],airinfo['stationName'],airinfo['pm25Value'],airinfo['pm25Grade'],airinfo['dataTime']])
    num =int(input('원하는 지역구 코드(숫자0~39)를 입력해주세요: 중랑구(36) >>>'))
    print('도시명', '지역구 코드', '미세먼지농도', '미세먼지등급', ' 현재시간')
    print(result_list[num])
